    from sklearn.metrics import cohen_kappa_score

    # æå–æ‰€æœ‰æ ‡æ³¨è€…çš„æ ‡æ³¨ç»“æœ
    annotator_labels = {}
    for ann in annotations:
        annotator_id = ann['annotator_id']
        if annotator_id not in annotator_labels:
            annotator_labels[annotator_id] = []
        annotator_labels[annotator_id].append(ann['label'])

    # è®¡ç®—ä¸¤ä¸¤ä¹‹é—´çš„Kappaç³»æ•°
    kappa_scores = []
    annotator_ids = list(annotator_labels.keys())

    for i in range(len(annotator_ids)):
        for j in range(i+1, len(annotator_ids)):
            labels_i = annotator_labels[annotator_ids[i]]
            labels_j = annotator_labels[annotator_ids[j]]
            kappa = cohen_kappa_score(labels_i, labels_j)
            kappa_scores.append(kappa)

    return sum(kappa_scores) / len(kappa_scores) if kappa_scores else 0
